import Vue from "vue";
import Router from "vue-router";
import Home from "./views/Home";
import About from "./views/About";

Vue.use(Router);
const routes = [
  {
    path: "/",
    name: "Home",
    component: Home,
  },
  {
    path: "/about",
    name: "About",
    component: About,
  },
];
export default function createRouter() {
  return new Router({
    mode: "history",
    routes,
  });
}

const items = [
  { id: 1, name: "吃饭" },
  { id: 2, name: "睡觉" },
  { id: 3, name: "打豆豆" },
];

export function fetchAll(id) {
  return new Promise((resolve, reject) => {
    setTimeout(() => {
      return resolve(items);
    }, 1500);
  });
}

export function fetchItem(id) {
  const item = items.find((i) => i.id == id);
  return new Promise((resolve, reject) => {
    setTimeout(() => {
      return resolve(item);
    }, 1500);
  });
}

import Vue from "vue";
import Vuex from "vuex";
import { fetchAll, fetchItem } from "./api";

Vue.use(Vuex);

export default function createStore() {
  return new Vuex.Store({
    state: {
      todoList: null,
      todoItem: null,
    },
    actions: {
      fetchAll({ commit }) {
        return fetchAll().then((todos) => {
          commit("setTodoList", todos);
        });
      },
      fetchItem({ commit }, id) {
        return fetchItem(id).then((item) => {
          commit("setItem", item);
        });
      },
    },
    mutations: {
      setTodoList(state, items) {
        state.todoList = items;
      },
      setItem(state, item) {
        state.todoItem = item;
      },
    },
  });
}

import Vue from "vue";
import App from "./App.vue";
import createRouter from "./createRouter";
import createStore from "./createStore";

export default function createApp(context) {
  const router = createRouter();
  const store = createStore();
  const app = new Vue({
    router,
    store,
    render: (h) => h(App),
  });
  return {
    router,
    store,
    app,
  };
}

import createApp from "./createApp";

export default (context) => {
  // 因为有可能会是异步路由钩子函数或组件，所以我们将返回一个 Promise，
  // 以便服务器能够等待所有的内容在渲染前就已经准备就绪。
  return new Promise((resolve, reject) => {
    const { app, router, store } = createApp();
    // 设置服务器端 router 的位置
    router.push(context.url);
    // onReady 等到 router 将可能的异步组件和钩子函数解析完
    router.onReady(() => {
      const matchedComponents = router.getMatchedComponents();
      console.log("matchedComponents", matchedComponents);
      // 匹配不到的路由，执行 reject 函数，并返回 404
      if (!matchedComponents.length) {
        return reject({
          url: context.url,
          code: 404,
        });
      }
      // 对所有匹配的路由组件调用 `asyncData()`
      Promise.all(
        matchedComponents.map((Component) => {
          if (Component.asyncData) {
            console.log("Component.asyncData", Component.asyncData);
            return Component.asyncData({
              store,
              route: router.currentRoute,
            });
          }
        })
      )
        .then(() => {
          console.log("store.state", store.state);
          // 当使用 template 时，context.state 将作为 window.__INITIAL_STATE__ 自动嵌入到最终的 HTML 中，方便后面客户端激活数据
          context.state = store.state;
          resolve(app);
        })
        .catch(reject);
    }, reject);
  });
};

